CREATE TABLE IF NOT EXISTS cdm_ingestion_logs (id BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY, vault_id VARCHAR(128) NOT NULL, namespace VARCHAR(64) NOT NULL, filename VARCHAR(255) NULL, report_week INT NULL, report_year INT NULL, status VARCHAR(50) DEFAULT 'anchored', created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
